let inputScreen=document.querySelector(".input-screen");function updateColor(){inputScreen.value<0||inputScreen.value>100?inputScreen.style.border="2px solid red":inputScreen.style.border="2px solid #fff"}updateScreen=()=>{let e=document.querySelector(".input-range");document.querySelector(".input-screen").value=e.value,updateColor()},updateRange=()=>{document.querySelector(".input-range").value=document.querySelector(".input-screen").value,updateColor()};